		this.base_url = base_url;
		if(base_url==null)
			return;
		var current = window.location.href.toString().split('#');
		if(current.length>1){
			link_url = current[1];
		}
		else{
			if(current[0]==this.getBaseUrl()){//es el home
				return;
			}
			current = current[0].split(this.getBaseUrl());
			if(current.length>1){
				link_url = current[1];
			}
			else{
				return;
			}
		}
